import{_ as a,o as e,c as r,d as i}from"./app.9673dcd9.js";var p="/assets/8bd13a84ba4342eb890ed456d0c1143c.f92bba37.png";const t={},d=i('<h3 id="\u4EA7\u54C1\u662F\u6700\u597D\u7684\u8425\u9500-\u5360\u9886\u7528\u6237\u5FC3\u667A" tabindex="-1"><a class="header-anchor" href="#\u4EA7\u54C1\u662F\u6700\u597D\u7684\u8425\u9500-\u5360\u9886\u7528\u6237\u5FC3\u667A" aria-hidden="true">#</a> \u4EA7\u54C1\u662F\u6700\u597D\u7684\u8425\u9500\uFF1A\u5360\u9886\u7528\u6237\u5FC3\u667A</h3><p>\u82F9\u679C\u7684\u201C\u767D\u96EA\u201D\u8BBE\u8BA1\u98CE\u683C\u5960\u5B9A\u8005\uFF0C\u9752\u86D9\u8BBE\u8BA1\u7684\u827E\u65AF\u6797\u683C\uFF1A\u5F62\u5F0F\u8FFD\u968F\u60C5\u7EEA\u4EF7\u503C\uFF08\u800C\u4E0D\u662F\u8FFD\u968F\u529F\u80FD\uFF09 \u4F8B\u5982\uFF1A \u82F9\u679C\u53D1\u5E03\u7684AR\u773C\u955C\uFF0C\u4F5C\u4E3A\u7528\u6237\u7684\u6211\uFF0C\u5373\u4F7F\u77E5\u9053\u8D35\uFF0C\u4E0D\u8FC7\u8FD8\u662F\u60F3\u4E70\uFF0C\u80FD\u6311\u52A8\u7528\u6237\u7684<strong>\u60C5\u7EEA</strong>\u624D\u662F\u7EC8\u6781\u5F62\u6001</p><p>\u771F\u5B9E\u7684\u7528\u6237\u5C31\u662F\u8FD9\u4E48<strong>\u80A4\u6D45</strong>\uFF1A\u6211\u770B\u5230\u4E86\uFF0C\u5FC3\u52A8\u4E86\uFF0C\u6709\u94B1\u60F3\u4E70\u5C31\u4E70\u4E86</p><p>\u50CF\u662F\u82F9\u679C\u65E9\u671F\u7684\u4EA7\u80FD\u4E0D\u591F\uFF0C\u94FA\u8D27\u6E20\u9053\u4E0D\u591F\u591A\uFF0C\u7136\u9E45\u7528\u6237\u613F\u610F\u7B49 \u4E00\u4E2A95\u5206\u7684\u4F18\u70B9\u6BD410\u4E2A70\u5206\u7684\u957F\u5904\u8981\u66F4\u52A0\u5438\u5F15\u4EBA\uFF0C\u8FD8\u662F\u8981\u6293\u4F4F\u7528\u6237\u7684\u6838\u5FC3\u9700\u6C42</p><p>\u6D88\u8D39\u5347\u7EA7\uFF1A\u4E2A\u4EBA\u613F\u610F\u4ED8\u51FA\u66F4\u9AD8\u7684\u6210\u672C\u8D2D\u4E70\u4E0E\u81EA\u6211\u4EF7\u503C\u76F8\u5339\u914D\u7684\u4EA7\u54C1\u3002 \u8D2D\u4E70\u5373\u662F\u4E00\u4E2A\u8EAB\u4EFD\u7684\u8BA4\u540C\uFF0C\u8D2D\u4E70\u4E5F\u662F\u4E00\u79CD\u610F\u89C1\u8868\u8FBE\u3002\uFF08\u5373\u82B1\u94B1\u4E70\u7237\u5F00\u5FC3\uFF09</p><h3 id="\u8BA9\u4F60\u7684\u4EA7\u54C1\u6210\u4E3A\u7528\u6237\u7684\u4E60\u60EF" tabindex="-1"><a class="header-anchor" href="#\u8BA9\u4F60\u7684\u4EA7\u54C1\u6210\u4E3A\u7528\u6237\u7684\u4E60\u60EF" aria-hidden="true">#</a> \u8BA9\u4F60\u7684\u4EA7\u54C1\u6210\u4E3A\u7528\u6237\u7684\u4E60\u60EF</h3><p>\u4E60\u60EF\uFF1A\u5C31\u662F\u4E00\u79CD\u201C\u5728\u60C5\u5883\u6697\u793A\u4E0B\u4EA7\u751F\u7684<strong>\u65E0\u610F\u8BC6</strong>\u884C\u4E3A\u201D\uFF0C\u662F\u6211\u4EEC\u51E0\u4E4E\u4E0D\u5047\u601D\u7D22\u5C31\u505A\u51FA\u7684\u4E3E\u52A8\u3002 \u57F9\u517B\u7528\u6237\u4E60\u60EF\u7684\u516C\u53F8\u4E0D\u4F9D\u8D56\u4E8E\u8D39\u7528\u9AD8\u6602\u7684\u8425\u9500\u7B56\u5212\uFF0C\u800C\u662F\u5C06\u4EA7\u54C1\u8BBE\u8BA1\u548C\u7528\u6237\u7684\u884C\u4E3A\u4E60\u60EF\u548C\u60C5\u611F\u72B6\u6001\u7D27\u5BC6\u76F8\u8FDE\u3002</p><h3 id="\u4E60\u60EF\u7684\u529B\u91CF-\u8BA9\u4F60\u7684\u4EA7\u54C1\u4ECE\u7EF4\u751F\u7D20\u53D8\u6210\u6B62\u75DB\u836F" tabindex="-1"><a class="header-anchor" href="#\u4E60\u60EF\u7684\u529B\u91CF-\u8BA9\u4F60\u7684\u4EA7\u54C1\u4ECE\u7EF4\u751F\u7D20\u53D8\u6210\u6B62\u75DB\u836F" aria-hidden="true">#</a> \u4E60\u60EF\u7684\u529B\u91CF\uFF1A\u8BA9\u4F60\u7684\u4EA7\u54C1\u4ECE\u7EF4\u751F\u7D20\u53D8\u6210\u6B62\u75DB\u836F</h3><p>\u8425\u9500\u8FC7\u7A0B\u4E2D\u628A\u75D2\u70B9\uFF08\u7EF4\u751F\u7D20\uFF09\u53D8\u6210\u89E3\u51B3\u75DB\u70B9\uFF08\u6B62\u75DB\u836F\uFF09\u4E00\u6837\u7684\u53D8\u5316</p><p>\u7528\u6237\u7EC8\u8EAB\u4EF7\u503C\uFF1A\u4E00\u4E2A\u7528\u6237\u5728\u5176\u6709\u751F\u4E4B\u5E74\u5FE0\u5B9E\u4F7F\u7528\u67D0\u4E2A\u4EA7\u54C1\u7684\u8FC7\u7A0B\u4E2D\u4E3A\u5176\u4ED8\u51FA\u7684\u6295\u8D44\u603B\u989D\u3002\u5F53\u7528\u6237\u5BF9\u67D0\u4E2A\u4EA7\u54C1\u4EA7\u751F\u4F9D\u8D56\u7684\u65F6\u5019\uFF0C\u4F7F\u7528\u65F6\u95F4\u4F1A\u5EF6\u957F\uFF0C\u4F7F\u7528\u9891\u7387\u4F1A\u589E\u52A0\uFF0C\u6700\u7EC8\u54E6\u90A3\u4E2A\u80E1\u7EC8\u8EAB\u4EF7\u503C\u4E5F\u4F1A\u63D0\u9AD8\u3002</p><p>\u4E00\u4E2A\u54C1\u724C\u7684\u8861\u91CF\u6807\u51C6\u5C31\u662F\u5B9A\u4EF7\u6743\uFF0C\u4F60\u80FD\u4E0D\u80FD\u6DA8\u4EF7\uFF0C\u6DA8\u4EF7\u4E5F\u4E0D\u4F1A\u5F71\u54CD\u7528\u6237\u6D41\u5931 \u54C1\u724C\u7684\u529B\u91CF\u5C31\u662F\u7528\u6237\u5BF9\u4EA7\u54C1\u7684\u4F9D\u8D56\uFF0C\u4EF7\u683C\u654F\u611F\u7684\u7528\u6237\u4E5F\u4F1A\u9009\u62E9</p><p>\u4EBA\u90FD\u6709\u52A3\u6839\u6027\uFF0C\u5F62\u6210\u4F9D\u8D56\u540E\u518D\u6539\u4F1A\u5F88\u96BE \u65B0\u4EA7\u54C1\u7684\u80DC\u51FA = \u65B0\u529F\u80FD &gt;&gt;&gt;\uFF08\u65E7\u529F\u80FD\uFF09- \u4E60\u60EF</p><blockquote><p>\u4E60\u60EF = \u9891\u7387 + \u611F\u77E5\u4EF7\u503C</p></blockquote><h3 id="\u4E0A\u763E\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u4E0A\u763E\u6A21\u578B" aria-hidden="true">#</a> \u4E0A\u763E\u6A21\u578B</h3><p>\u4E0A\u763E\u6A21\u578B\uFF08the Hook Model):\u89E6\u53D1-\u884C\u52A8-\u591A\u53D8\u7684\u5956\u8D4F-\u6295\u5165 Hook up:\u5438\u5F15\u7528\u6237\u5C31\u50CF\u8C08\u604B\u7231\uFF0C\u52FE\u642D\u5BF9\u773C\u5C31\u884C \u8DEA\u8214\u7528\u6237\u662F\u6CA1\u6709\u524D\u9014\u7684 \u4F4E\u4EF7\u4E0D\u662F\u58C1\u5792\uFF0C\u4F4E\u6210\u672C\u624D\u662F\uFF0C\u540D\u521B\u4F18\u54C19.9\u7684\u96E8\u4F1E\u5305\u8D5A50%\u7684\u539F\u56E0\u5728\u4E8E\u4E0B\u5355\u91CF\u7EA7\uFF0810w vs 1000w)\u4E0D\u540C</p><h4 id="\u89E6\u53D1-\u884C\u4E3A\u53EC\u5524" tabindex="-1"><a class="header-anchor" href="#\u89E6\u53D1-\u884C\u4E3A\u53EC\u5524" aria-hidden="true">#</a> \u89E6\u53D1\uFF1A\u884C\u4E3A\u53EC\u5524</h4><p><strong>\u5916\u90E8\u89E6\u53D1</strong>\uFF1A\u901A\u8FC7\u53D1\u8D77\u884C\u52A8\u53EC\u5524\u6765\u6697\u793A\u7528\u6237\uFF0C\u8FD9\u662F\u6253\u9020\u4E60\u60EF\u517B\u6210\u7C7B\u6280\u672F\u7684\u7B2C\u4E00\u6B65\u3002\u5C31\u50CF \u8DEF\u8FB9\u770B\u5230 M \u7684\u62DB\u724C\uFF0C\u53EC\u5524\u4EBA\u8FDB\u53BB\u4E70\u6C49\u5821</p><p><strong>\u5185\u90E8\u89E6\u53D1</strong>\uFF1A\u5F53\u67D0\u4E2A\u4EA7\u54C1\u548C\u4F60\u7684\u601D\u60F3\u3001\u60C5\u611F\u6216\u8005\u662F\u539F\u672C\u5DF2\u7ECF\u6709\u7684\u5E38\u89C4\u6D3B\u52A8\u53D1\u9001\u5BC6\u5207\u5173\u8054\uFF0C\u4F60\u770B\u4E0D\u5230\uFF0C\u6478\u4E0D\u7740\uFF0C\u4F46\u4ED6\u81EA\u52A8\u51FA\u73B0\u5728\u4F60\u7684\u8111\u888B\u91CC\u3002</p><p>\u5185\u90E8\u89E6\u53D1\u7684\u9600\u95E8\u5C31\u662F<strong>\u60C5\u7EEA</strong><img src="'+p+'" alt="23228ad26030264460f590550fad2404.png"></p><p>\u6050\u60E7\u611F\u624D\u662F\u5979\u6700\u5927\u7684\u5185\u90E8\u89E6\u53D1\uFF0C\u4F60\u8BBE\u8BA1\u7684\u4EA7\u54C1\u9700\u8981\u6293\u4F4F\u8FD9\u4E00\u70B9\u53BB\u505A\u6307\u5BFC\u3002</p><h4 id="\u884C\u52A8-\u4EBA\u4EEC\u5728\u671F\u5F85\u5956\u8D4F\u65F6\u7684\u76F4\u63A5\u53CD\u5E94" tabindex="-1"><a class="header-anchor" href="#\u884C\u52A8-\u4EBA\u4EEC\u5728\u671F\u5F85\u5956\u8D4F\u65F6\u7684\u76F4\u63A5\u53CD\u5E94" aria-hidden="true">#</a> \u884C\u52A8\uFF1A\u4EBA\u4EEC\u5728\u671F\u5F85\u5956\u8D4F\u65F6\u7684\u76F4\u63A5\u53CD\u5E94</h4><p>\u4E00\u4E2A\u884C\u4E3A\u7684\u524D\u63D0\u6761\u4EF6\u662F \u52A8\u4F5C\u8981\u7B80\u5355\uFF0C\u590D\u6742\u7A0B\u5EA6\u8D8A\u4F4E\uFF0C\u8D8A\u6613\u4E8E\u91CD\u590D</p><p>\u884C\u52A8\u8981\u7D20\uFF1A\u5145\u5206\u7684\u52A8\u673A\uFF0C\u5B8C\u6210\u884C\u4E3A\u7684\u80FD\u529B\uFF0C\u4FC3\u4F7F\u4EBA\u4EEC\u4ED8\u8BF8\u65B0\u7684\u7684\u89E6\u53D1\u52A8\u4F5C</p><p>\u6838\u5FC3\u52A8\u673A\uFF1A</p><ol><li>\u8FFD\u6C42\u5FEB\u4E50\uFF0C\u9003\u907F\u75DB\u82E6</li><li>\u8FFD\u6C42\u5E0C\u671B\uFF0C\u9003\u907F\u6050\u60E7</li><li>\u8FFD\u6C42\u8BA4\u540C\uFF0C\u9003\u907F\u6392\u65A5</li></ol><p>\u538C\u6076\u635F\u5931\uFF1A\u9003\u907F\u7684\u8981\u7D20&gt;&gt; \u8FFD\u6C42\u7684\u8981\u7D20</p><h4 id="\u80FD\u529B-\u80FD\u4E0D\u80FD\u505A\u5230-\u7B80\u5355\u624D\u80FD\u505A\u5230" tabindex="-1"><a class="header-anchor" href="#\u80FD\u529B-\u80FD\u4E0D\u80FD\u505A\u5230-\u7B80\u5355\u624D\u80FD\u505A\u5230" aria-hidden="true">#</a> \u80FD\u529B\uFF1A\u80FD\u4E0D\u80FD\u505A\u5230-\u7B80\u5355\u624D\u80FD\u505A\u5230</h4><p>\u5F71\u54CD\u4EFB\u52A1\u96BE\u6613\u5EA6\u7684\u8981\u7D20\uFF1A</p><ol><li>\u65F6\u95F4</li><li>\u91D1\u94B1</li><li>\u4F53\u529B</li><li>\u8111\u529B</li><li>\u793E\u4F1A\u504F\u5DEE\uFF1A\u4ED6\u4EBA\u7684\u63A5\u53D7\u7A0B\u5EA6</li><li>\u975E\u5E38\u89C4\u6027\uFF1A\u548C\u5176\u4ED6\u5E38\u89C4\u6D3B\u52A8\u7684\u5339\u914D\u7A0B\u5EA6\u548C\u77DB\u76FE\u7A0B\u5EA6</li></ol><p>\u52A8\u673A\u548C\u80FD\u529B\u7684\u4F18\u5148\u7EA7\uFF1A\u5148\u89E3\u51B3\u80FD\u529B\u95EE\u9898\uFF0C\u964D\u4F4E\u7528\u6237\u7684\u4F7F\u7528\u95E8\u69DB \u7A00\u7F3A\u6548\u5E94\uFF0C\u73AF\u5883\u6548\u5E94\uFF0C\u951A\u5B9A\u6548\u5E94\uFF0C\u8D60\u5238\u6548\u5E94</p><h4 id="\u591A\u53D8\u7684\u5956\u52B1\u673A\u5236-\u968F\u673A\u7684\u6FC0\u53D1\u4F7F\u7528\u6B32" tabindex="-1"><a class="header-anchor" href="#\u591A\u53D8\u7684\u5956\u52B1\u673A\u5236-\u968F\u673A\u7684\u6FC0\u53D1\u4F7F\u7528\u6B32" aria-hidden="true">#</a> \u591A\u53D8\u7684\u5956\u52B1\u673A\u5236\uFF1A\u968F\u673A\u7684\u6FC0\u53D1\u4F7F\u7528\u6B32</h4><p>\u65AF\u91D1\u7EB3\u9E3D\u5B50\u5B9E\u9A8C\uFF1A\u968F\u673A\u6027\u8BA9\u538B\u52A8\u64CD\u7EB5\u6746\u7684\u6B21\u6570\u660E\u663E\u589E\u52A0</p><p>\u9A71\u4F7F\u6211\u4EEC\u884C\u52A8\u7684\u4E0D\u662F\u5956\u52B1\u672C\u8EAB\uFF0C\u800C\u662F\u6E34\u671B\u5956\u52B1\u65F6\u4EA7\u751F\u7684\u90A3\u4EFD\u8FEB\u5207\u9700\u8981\u3002</p><p>\u793E\u4EA4\u916C\u8D4F\uFF1A\u6765\u81EA\u4ED6\u4EBA\u7684\u8BA4\u540C \u730E\u7269\u916C\u8D4F\uFF1A\u8D44\u6E90\uFF0C\u91D1\u94B1\u548C\u4FE1\u606F\uFF08\u8001\u864E\u673A\uFF0C\u6296\u97F3\uFF09 \u81EA\u6211\u916C\u8D4F\uFF1A\u64CD\u63A7\u611F\uFF0C\u6210\u5C31\u611F\uFF0C\u80DC\u4EFB\u611F\uFF08\u6E38\u620F\u52CB\u7AE0\uFF09</p><blockquote><p>\u5F53\u7ED9\u7684\u94B1\u4E0D\u591F\u591A\u7684\u65F6\u5019\uFF0C\u4E0D\u8981\u7ED9\u94B1\u4F5C\u4E3A\u5956\u52B1\uFF0C\u5BFB\u6C42\u5176\u4ED6\u4EF7\u503C\u5956\u8D4F</p></blockquote><h4 id="\u6295\u5165-\u7528\u6237\u4ED8\u51FA\u8D8A\u591A-\u7C98\u6027\u8D8A\u9AD8" tabindex="-1"><a class="header-anchor" href="#\u6295\u5165-\u7528\u6237\u4ED8\u51FA\u8D8A\u591A-\u7C98\u6027\u8D8A\u9AD8" aria-hidden="true">#</a> \u6295\u5165\uFF1A\u7528\u6237\u4ED8\u51FA\u8D8A\u591A\uFF0C\u7C98\u6027\u8D8A\u9AD8</h4><p>\u5B9C\u5BB6\u6548\u5E94\uFF1A\u4ED8\u51FA\u8FC7\u52B3\u52A8\u7684\u4EBA\u7ED9\u81EA\u5DF1\u7684\u4F5C\u54C1\u4F1A\u9644\u52A0\u66F4\u591A\u7684\u4EF7\u503C\u3002 \u8981\u8B66\u60D5\u8FD9\u4E00\u70B9\uFF0C\u5E02\u573A\u4E0D\u4F1A\u770B\u8FD9\u4E2A\u3002</p><p>\u884C\u4E3A\u524D\u540E\u4E00\u81F4\u6548\u5E94\uFF1A\u5728\u7528\u6237\u7B54\u5E94\u4E00\u4E2A\u5C0F\u6761\u4EF6\u540E\uFF0C\u5F97\u5BF8\u8FDB\u5C3A\u7684\u63D0\u51FA\u66F4\u5927\u7684\u6761\u4EF6\uFF0C\u7528\u6237\u7EF4\u62A4\u9762\u5B50\u6709\u6982\u7387\u4F1A\u8BA4 \u7B54\u5E94\u653E\u5C0F\u7684\uFF0850\u516C\u5206\uFF09\u5E7F\u544A\u724C-\u5F97\u5BF8\u8FDB\u5C3A\u7684\u653E\u7F6E3\u7C73\u7684\u5E7F\u544A\u724C</p>',38),h=[d];function n(o,s){return e(),r("div",null,h)}var c=a(t,[["render",n],["__file","index.html.vue"]]);export{c as default};
