import{_ as a,o as e,c as i,d as p}from"./app.9673dcd9.js";var r="/assets/e98fa1ce8cb743909902fa381c8a7a40.4ecc65a7.png",l="/assets/dcfa22ddbcf44c8c98b9bdbb9c286d4d.ceba5172.png",d="/assets/730bd13ea5584a21a35014227c33a52f.014394fa.png",o="/assets/6a5e9a04176649768d007d267e7295e2.ed83365d.png",t="/assets/391e4cdbda41492e9201efd5e3eb05c3.b24933e8.png";const h={},c=p('<h2 id="\u4EF7\u683C\u662F\u76C8\u5229\u7684\u5173\u952E" tabindex="-1"><a class="header-anchor" href="#\u4EF7\u683C\u662F\u76C8\u5229\u7684\u5173\u952E" aria-hidden="true">#</a> \u4EF7\u683C\u662F\u76C8\u5229\u7684\u5173\u952E</h2><blockquote><p>\u5168\u4E16\u754C\u53EA\u67098%\u7684\u516C\u53F8\u4F1A\u7CFB\u7EDF\u6027\u8003\u8651\u5B9A\u4EF7</p></blockquote><p>\u5B9A\u4EF7\u80CC\u540E\u7684\u7528\u6237\u7B5B\u9009\u548C\u4EF7\u503C\u5851\u9020</p><p>\u5B9A\u4EF7\u7684\u5927\u5751\uFF1A</p><ul><li>\u57FA\u4E8E\u6210\u672C\u5B9A\u4EF7</li><li>\u7784\u51C6\u7ADE\u4E89\u5BF9\u624B\u5B9A\u4EF7</li><li>\u4E3A\u4E86\u8FFD\u6C42\u9500\u91CF\u5B9A\u4F4E\u4EF7</li></ul><h3 id="\u57FA\u4E8E\u6210\u672C\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C" tabindex="-1"><a class="header-anchor" href="#\u57FA\u4E8E\u6210\u672C\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C" aria-hidden="true">#</a> \u57FA\u4E8E\u6210\u672C\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C\uFF1F</h3><ol><li>\u4F7F\u7528\u6700\u7B80\u5355\uFF08\u7B97\u6BD4\u4F8B\uFF0C\u52A0\u6210\u672C\u7FFB\u500D\u7387\uFF09</li><li>\u770B\u8D77\u6765\u6700\u516C\u5E73\uFF08\u4EBA\u503E\u5411\u4E8E\u8FFD\u6C42\u2018\u516C\u5E73\u2019\uFF09</li><li>\u8D22\u52A1\u6700\u201C\u6B63\u786E\u201D(\u8D22\u52A1\u7684\u653F\u6CBB\u6B63\u786E)</li></ol><blockquote><p>\u8305\u53F0\u6210\u672C\u51E0\u5341\u5757\uFF0C\u5B9A\u4EF72000\u591A</p></blockquote><p>\u95EE\u9898\uFF1A</p><ul><li>\u7528\u6237\u6839\u672C\u4E0D\u77E5\u4E4E\u4F60\u7684\u6210\u672C \u7528\u6237\u5927\u6982\u7387\u4E0D\u77E5\u9053\u4F60\u7684\u6210\u672C\uFF0C\u800C\u4E14\u6211\u4EEC\u4F5C\u4E3A\u7528\u6237\u4E5F\u4E0D\u5728\u4E4E\u6210\u672C\uFF0C\u800C\u662F\u5728\u4E8E\u8FD9\u4E2A\u4EA7\u54C1\u662F\u4E0D\u662F\u7528\u7684\u723D\uFF0C\u5FC3\u60C5\u597D\u4E0D\u597D</li><li>\u6210\u672C\u968F\u7740\u9500\u91CF\u6539\u53D8\uFF0C\u6839\u672C\u65E0\u6CD5\u7CBE\u786E\u7B97\u6E05\u695A \u50CF\u662F\u674E\u4F73\u5947\u4E0D\u51FA\u540D\u7684\u65F6\u5019\uFF0C\u5751\u4F4D\u8D39\u57FA\u672C\u6CA1\u6709\uFF0C\u73B0\u5728\u51E0\u767E\u4E07\u3002\u3002</li><li>\u6700\u8981\u547D\u7684\uFF1A\u6210\u672C\u5B9A\u4EF7\u4EE5\u81EA\u6211\u4E3A\u4E2D\u5FC3\uFF0C\u800C\u4E0D\u662F\u4EE5\u5E02\u573A\u4E3A\u4E2D\u5FC3\uFF0C\u4EF7\u683C\u53D6\u51B3\u4E8E\u7528\u6237\u4E70\u4E0D\u4E70\u5E10</li></ul><blockquote><p>\u201C\u8584\u5229\u591A\u9500\u201D\u53EA\u662F\u4E2A\u7B26\u5408\u76F4\u89C9\uFF0C\u4F46\u5176\u5B9E\u5F88\u8FC7\u65F6\u7684\u89C2\u5FF5\u4E86\u3002</p></blockquote><p>\u5B9A\u4EF7\u4E0D\u7B49\u4E8E\u6210\u672C+\u5229\u6DA6 \u6210\u672C\u53EA\u80FD\u544A\u8BC9\u6211\u4EEC\u6700\u4F4E\u4EF7\u683C\uFF0C\u800C\u4E0D\u662F\u6700\u5408\u9002\u7684\u4EF7\u683C</p><h4 id="\u82F9\u679Cvison-pro\u7684\u5B9A\u4EF7\u903B\u8F91\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u82F9\u679Cvison-pro\u7684\u5B9A\u4EF7\u903B\u8F91\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u82F9\u679CVison Pro\u7684\u5B9A\u4EF7\u903B\u8F91\u662F\u4EC0\u4E48\uFF1F</h4><p>25000/\u53F0 \u6210\u672C\u8F83\u8D35\uFF1A\u5E02\u573A\u666E\u901A\u6DB2\u6676\u663E\u793A\u5668\uFF0C\u82F9\u679C\u975E\u901A\u7528\u5FAE\u7F29OLED-300\u7F8E\u91D1 \u5806\u6599\uFF1A\u5E02\u573A1\u82AF\u72474\u6444\u50CF\u59341\u4F20\u611F\u5668\uFF0C\u82F9\u679C2\u82AF\u724712\u6444\u50CF\u59345\u4F20\u611F\u5668 \u76EE\u6807\u662F\u5954\u7740\u7B2C\u4E00\u53BB\u7684\uFF0C\u57FA\u672C\u4E0D\u8BA1\u6210\u672C\uFF0C\u800C\u662F\u548C\u5546\u4E1A\u6A21\u5F0F\u3001\u6218\u7565\u5F3A\u76F8\u5173</p><p><img src="'+r+'" alt="63ff9f876700fe91be86b7099c13359e.png"> \u8C03\u67E5\u4E86\u4E00\u4E0B\u82F9\u679C\u5934\u663E\u7684\u5236\u4F5C\u6210\u672C\uFF0C\u518D\u52A0\u4E0A\u7269\u6D41\u3001\u4ED3\u50A8\u3001\u8D27\u635F\u3001\u9000\u6362\u8D27\u3001\u4E0D\u826F\u7387\u3001\u4EE3\u7406\u5546\u7B49\uFF0C3500\u7F8E\u91D1\u7684\u4EF7\u683C\u771F\u7684\u53EF\u80FD\u6CA1\u6709\u5229\u6DA6\uFF01 \u90FD\u8BF4\u82F9\u679C\u4EA7\u54C1\u6BDB\u5229\u592A\u9AD8\uFF0C\u800C\u8FD9\u4E2A\u9879\u76EE\u6574\u4F53\u771F\u7684\u5F88\u4E0D\u201C\u82F9\u679C\u201D\uFF1A\u4EF7\u683C\u4E0D\u201C\u82F9\u679C\u201D\uFF0C\u5185\u5728\u8BBE\u8BA1\u4E0D\u201C\u82F9\u679C\u201D\uFF0C\u5916\u63A5\u7684\u90A3\u4E00\u57571\u4E07mAh\u7684\u5927\u7535\u6C60\u66F4\u4E0D\u201C\u82F9\u679C\u201D\u3002</p><p>\u6211\u770B\u5230\u7684\u662F\uFF0C\u5B83\u5F00\u8F9F\u4E86\u8FD9\u4E2A\u5168\u65B0\u7684\u8D5B\u9053\u3001\u6CA1\u6709\u7ADE\u4E89\u5BF9\u624B\uFF0C\u4E3A\u4E86\u8BA9\u66F4\u591A\u7684\u4EBA\u4F7F\u7528\u3001\u4E14\u7B2C\u4E00\u65F6\u95F4\u4E60\u60EF\u82F9\u679C\u3001\u63D0\u9AD8\u672A\u6765\u7ADE\u4E89\u5BF9\u624B\u201C\u8FC1\u79FB\u6210\u672C\u201D\u7684\u4E00\u7CFB\u5217\u5DE8\u5927\u7684\u8C03\u6574\uFF0C\u56E0\u5E02\u573A\u800C\u975E\u81EA\u6211\u7684\u8C03\u6574\u3002 \u6211\u76F8\u4FE1\u8FD9\u79CD\u5DE8\u5927\u7684\u7B56\u7565\u8C03\u6574\u548C\u524D\u671F\u6CA1\u6709\u5229\u6DA6\u7684\u5B9A\u4EF7\uFF0C\u4ECE\u957F\u671F\u65F6\u95F4\u8DE8\u5EA6\u4E0A\u6765\u8BB2\uFF0CROI\u4E00\u5B9A\u662F\u975E\u5E38\u975E\u5E38\u9AD8\u7684\u3002</p><h4 id="\u7279\u65AF\u62C9\u7684\u5B9A\u4EF7" tabindex="-1"><a class="header-anchor" href="#\u7279\u65AF\u62C9\u7684\u5B9A\u4EF7" aria-hidden="true">#</a> \u7279\u65AF\u62C9\u7684\u5B9A\u4EF7\uFF1A</h4><p>\u7B2C\u4E00\u4EE3 \u5BCC\u4EBA\u7684\u73A9\u5177\uFF0C\u73AF\u4FDD\u7684\u653F\u6CBB\u6B63\u786E\uFF08\u8BF4\u7ED9\u7528\u6237\u7684\u662F\u4EA7\u80FD\u4E0D\u591F\uFF0C\u591F\u73AF\u4FDD\uFF09-\u4EBA\u8BBE\u53CA\u54C1\u724C\u6545\u4E8B \u7B2C\u4E8C\u4EE3 \u5BCC\u88D5\u4E2D\u4EA7\u7684\u9762\u5B50\uFF08\u8BF4\u7ED9\u7528\u6237\u7684\u662F\u6280\u672F\u8FC7\u5173\u4E86\uFF0C\u8C0B\u798F\u5229\uFF09-\u4E2D\u4EA7\u4EBA\u7684\u4F53\u9762\uFF0C\u56E0\u4E3A\u7F8E\u56FD\u5BCC\u4EBA\u90FD\u4E70 \u7B2C\u4E09\u4EE3 \u666E\u901A\u4E2D\u4EA7\u7684\u6027\u4EF7\u6BD4\uFF08\u7535\u8F66\u7684\u6545\u4E8B\u8BB2\u5B8C\u4E86\uFF0C\u5E02\u573A\u7A33\u5B9A\uFF09</p><blockquote><p>\u5C0F\u516C\u53F8\u662F\u6CA1\u6709\u8D44\u683C\u70E7\u94B1\u964D\u4EF7\u8D70\u91CF\u7684</p></blockquote><h3 id="\u7784\u51C6\u7ADE\u4E89\u5BF9\u624B\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C" tabindex="-1"><a class="header-anchor" href="#\u7784\u51C6\u7ADE\u4E89\u5BF9\u624B\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C" aria-hidden="true">#</a> \u7784\u51C6\u7ADE\u4E89\u5BF9\u624B\u5B9A\u4EF7\u4E3A\u4EC0\u4E48\u6D41\u884C\uFF1F</h3><p>\u95EE\u9898\uFF1A</p><ol><li>\u7B80\u5355\uFF0C\u4E0D\u9700\u8981\u6DF1\u5EA6\u8C03\u7814</li><li>\u8C8C\u4F3C\u5B89\u5168\uFF0C\u8DDF\u7740\u7ADE\u54C1\u968F\u5927\u6D41</li></ol><p>\u9519\u8BEF\uFF1A</p><ol><li>\u6D88\u6781\u88AB\u52A8\uFF0C\u88AB\u7ADE\u54C1\u5236\u7EA6</li><li>\u5BB9\u6613\u9677\u5165\u6076\u6027\u7ADE\u4E89\uFF0C\u5927\u5BB6\u90FD\u6CA1\u94B1\u8D5A</li></ol><p>\u5C0F\u516C\u53F8\u503E\u5411\u4E8E\u6BD4\u7ADE\u54C1\u5B9A\u4EF7\u66F4\u4F4E\u4E00\u70B9\uFF0C\u800C\u4E0D\u6562\u5B9A\u9AD8\u4EF7\uFF0C\u7136\u540E\u7ADE\u54C1\u53C8\u4F1A\u964D\u4EF7\u966A\u4F60\u73A9</p><ul><li>\u4F60\u53EA\u770B\u5230\u5BF9\u624B\u7684\u4EF7\u683C\uFF0C\u770B\u4E0D\u5230\u80CC\u540E\u7684\u6218\u7565\u548C\u80FD\u529B \u4F60\u5B8C\u5168\u4E0D\u77E5\u9053\u5BF9\u624B\u7684\u4EA7\u54C1\u6838\u5FC3\u662F\u4EC0\u4E48\uFF0C\u76F4\u63A5\u6284\u4EF7\u683C\u662F\u4E0D\u884C\u7684</li></ul><p>\u4F60\u4EE5\u4E3A\u7684\u968F\u5927\u6D41\u662F\u6700\u5B89\u5168\u7684\uFF0C\u6CA1\u6709\u5DEE\u5F02\u5316\u624D\u6700\u5371\u9669</p><ul><li>\u4E3A\u4E86\u8FFD\u6C42\u9500\u91CF\u5B9A\u4F4E\u4EF7 \u628A\u4EE5\u5356\u7684\u591A\uFF08\u6536\u5165\u3001\u9500\u91CF\u3001\u5E02\u573A\u4EFD\u989D\uFF09\u4E3A\u76EE\u6807\uFF0C\u800C\u4E0D\u662F\u628A\u8D5A\u7684\u591A\uFF08\u76C8\u5229\uFF09\u5F53\u4F5C\u76EE\u6807</li></ul><blockquote><p>\u8425\u6536\u505A\u52301\u4EBF\uFF0C\u5E74\u5E95\u7684\u5229\u6DA6\u53EF\u80FD\u624D\u51E0\u767E\u4E07\uFF081%\u51C0\u5229\u6DA6\uFF09</p></blockquote><p>\u73B0\u91D1\u6D41\u4E0D\u5C5E\u4E8E\u5229\u6DA6\uFF0C\u4E0D\u5C5E\u4E8E\u4F60\u4E2A\u4EBA\u80FD\u81EA\u7531\u652F\u914D\u7684\u94B1\uFF0C\u5B83\u53EF\u80FD\u8981\u53D1\u5DE5\u8D44\uFF0C\u4ED8\u5DE5\u7A0B\u6B3E\uFF0C\u53EA\u80FD\u8BF4\u662F\u6551\u547D\u7684</p><p>\u4E91\u5357\u767D\u836F\u521A\u5F00\u59CB6\u5143\u6839\u672C\u5356\u4E0D\u52A8\uFF0C\u540E\u9762\u63D0\u523048\u5356\u7206\u4E86\u3002\u3002</p><h3 id="\u4E3A\u4EC0\u4E48-\u5B9A\u4F4E\u4EF7-\u6210\u4E3A\u4E60\u60EF" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48-\u5B9A\u4F4E\u4EF7-\u6210\u4E3A\u4E60\u60EF" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u201C\u5B9A\u4F4E\u4EF7\u201D\u6210\u4E3A\u4E60\u60EF\uFF1F</h3><ol><li>\u201C\u8584\u5229\u591A\u9500\u201D\u610F\u8BC6\u6839\u6DF1\u8482\u56FA</li><li>\u5BF9\u9500\u91CF\u654F\u611F\uFF0C\u5BF9\u5229\u6DA6\u4E0D\u654F\u611F</li></ol><blockquote><p>\u5982\u679C\u5E02\u573A\u4EFD\u989D\u4E0B\u964D10%\uFF0C\u8BB8\u591A\u4EBA\u4F1A\u53D7\u5230\u4E25\u5389\u60E9\u7F5A\uFF1B\u4F46\u5229\u6DA6\u4E0B\u964D20%\uFF0C\u6CA1\u6709\u4EBA\u4F1A\u5728\u4E4E</p></blockquote><p>\u89C4\u6A21\u5E26\u6765\u5229\u6DA6\u7684\u65F6\u4EE3\u5DF2\u7ECF\u8FC7\u53BB\u4E86 <em>\u5B9D\u6D01</em>\u7684\u86EE\u8352\u65F6\u4EE3\uFF0C\u5F53\u65F6\u6211\u4EEC\u9009\u62E9\u9879\u53EA\u6709\u6D77\u98DE\u4E1D\u4E00\u7C7B\u7684\u5927\u4F17\u54C1\u724C \u73B0\u5728\u9009\u62E9\u9879\u6709\u5F88\u591A\uFF0C\u6211\u4EEC\u7684\u601D\u7EF4\u8FD8\u5728\u4E0A\u4E2A\u65F6\u4EE3</p><h4 id="\u7F51\u7EA2\u65B0\u6D88\u8D39\u72EC\u89D2\u517D\u4E3A\u4EC0\u4E48\u6B7B\u7684\u5FEB" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EA2\u65B0\u6D88\u8D39\u72EC\u89D2\u517D\u4E3A\u4EC0\u4E48\u6B7B\u7684\u5FEB" aria-hidden="true">#</a> \u7F51\u7EA2\u65B0\u6D88\u8D39\u72EC\u89D2\u517D\u4E3A\u4EC0\u4E48\u6B7B\u7684\u5FEB\uFF1F</h4><p>\u7F51\u7EA2\u54C1\u90FD\u662F\u706B\u5F97\u5FEB\u6B7B\u5F97\u66F4\u5FEB\uFF0C\u56E0\u4E3A\u4E0D\u50CF\u4E92\u8054\u7F51\u4EA7\u54C1\u5177\u6709\u7684\u7279\u6027 \u4E92\u8054\u7F51\u7684\u7279\u6709\u5C5E\u6027\uFF1A\u7528\u6237\u7684\u65F6\u95F4\u6210\u672C\u548C\u7C98\u6027\u9AD8\uFF0C\u6709\u58C1\u5792 \u6D88\u8D39\u54C1\u8FD9\u4E2A\u54C1\u7C7B\u4E0B\uFF0C\u7528\u6237\u7684\u5FE0\u8BDA\u5EA6\u4E0D\u9AD8\uFF0C\u90A3\u4E2A\u6709\u6298\u6263\u7528\u6237\u5F80\u54EA\u8D70</p><blockquote><p>\u727A\u7272\u539F\u672C\u80FD\u8D5A\u5230\u7684\u5229\u6DA6\u53BB\u6362\u53D6\u9500\u91CF\uFF0C\u4ECA\u5929\u6BEB\u65E0\u610F\u4E49\uFF0C\u56E0\u4E3A\u7528\u6237\u4F1A\u8DDF\u7740\u6298\u6263\u4F18\u60E0\u8DD1</p></blockquote><p>\u60F3\u60F3\u98DE\u4E66\u548C\u817E\u8BAF\u4F1A\u8BAE\u8FD9\u4E9B\u65E9\u671F\u514D\u8D39\uFF0C\u73B0\u5728\u90FD\u5F00\u59CB\u6536\u8D39\u4E86\uFF0C\u56E0\u4E3A\u7528\u6237\u5728\u8FD9\u4E0A\u9762\u6709\u6C89\u6DC0\uFF0C\u8FC1\u79FB\u6210\u672C\u8DB3\u591F\u9AD8\uFF0C\u7528\u6237\u7C98\u6027\u9AD8</p><p>\u89C4\u6A21\u5316\u7B49\u4E8E\u89C4\u6A21\u5316\u5229\u6DA6\uFF0C\u800C\u4E0D\u662F\u89C4\u6A21\u5316\u8425\u6536</p><h2 id="\u5B9A\u4EF7-\u5229\u6DA6\u7684\u7EC8\u6781\u6760\u6746" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4EF7-\u5229\u6DA6\u7684\u7EC8\u6781\u6760\u6746" aria-hidden="true">#</a> \u5B9A\u4EF7\uFF1A\u5229\u6DA6\u7684\u7EC8\u6781\u6760\u6746</h2><p>\u9A71\u4F7F\u5229\u6DA6\u7684\u8981\u7D20\uFF1A \u4EF7\u683C &gt; \u9500\u91CF &amp; \u6210\u672C</p><p>\u6C83\u987F\u5546\u5B66\u9662\u7684\u6570\u636E\uFF1A \u6210\u672C\u964D\u4F4E1%\uFF0C\u76C8\u5229\u80FD\u529B2.45% \u9500\u552E\u989D\u63D0\u53471%\uFF0C\u76C8\u5229\u80FD\u529B\u63D0\u53473.28% <strong>\u4EF7\u683C\u63D0\u53471%\uFF0C\u76C8\u5229\u80FD\u529B\u63D0\u534710.29%</strong></p><p>\u56E0\u4E3A\u8425\u6536\u548C\u6210\u672C\u66F4\u5BB9\u6613\u91CF\u5316\u548C\u8BA1\u7B97\u7684\u4E8B\u60C5\uFF0C\u7BA1\u7406\u8005\u5F80\u5F80\u5173\u6CE8\u8425\u6536\u591A\u4F59\u5229\u6DA6</p><p>\u4E0D\u8981\u8FC7\u5EA6\u63A7\u5236\u6210\u672C\uFF0C\u5411\u4EF7\u683C\u53BB\u8981\u5229\u6DA6\uFF0C\u800C\u4E0D\u662F\u4ECE\u6210\u672C\u53BB\u7701\u5229\u6DA6\uFF1B \u56E0\u4E3A\u6210\u672C\u538B\u4E0B\u53BB\uFF0C\u8D28\u91CF\u53EF\u80FD\u4E0B\u964D\uFF0C\u7528\u6237\u5927\u6982\u7387\u79BB\u5F00</p><p>\u8584\u5229\u591A\u9500\u662F\u5C0F\u519C\u65F6\u4EE3\u7684\u601D\u7EF4\uFF0C\u5927\u91CF\u7684\u8D5B\u9053\u662F\u7EA2\u6D77\uFF0C\u5BF9\u5E02\u573A\u5360\u6709\u7387\u7684\u6781\u81F4\u8FFD\u6C42\u6B63\u662F\u5BFC\u81F4\u5F88\u591A\u4F01\u4E1A\u4F4E\u5229\u6DA6/\u65E0\u5229\u6DA6\u7684\u4E3B\u8981\u539F\u56E0</p><p><strong>\u4F20\u7EDF</strong>\u5E02\u573A\u903B\u8F91\uFF1A \u8D62\u5F97\u5E02\u573A\u5360\u6709\u7387\uFF0C\u5229\u6DA6\u968F\u4E4B\u800C\u6765 <strong>\u73B0\u5B9E</strong>\u5E02\u573A\u903B\u8F91\uFF1A \u5BA2\u6237\u771F\u6B63\u60F3\u8981\u7684\u4EF7\u503C\u662F\u4EC0\u4E48 \u6211\u4EEC\u9700\u8981\u505A\u5230\u7684\u662F\u5982\u4F55\u628A\u5229\u6DA6\u505A\u9AD8\uFF0C\u5229\u6DA6\u8D77\u6765\u7684\u524D\u63D0\u4E0B\u624D\u662F\u5982\u4F55\u8D62\u5F97\u5E02\u573A\u5360\u6709\u7387</p><p>\u4EE5\u4EA7\u54C1\u4E3A\u4E2D\u5FC3\u3001\u8FFD\u6C42\u5E02\u573A\u5360\u6709\u7387\u7684\u65E7\u4E16\u754C\u5DF2\u7ECF\u53D8\u6210\u4E86 <strong>\u5BA2\u6237</strong>\u548C\u5E02\u573A\u9700\u6C42</p><h2 id="\u5B9A\u4EF7\u6280\u5DE7" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4EF7\u6280\u5DE7" aria-hidden="true">#</a> \u5B9A\u4EF7\u6280\u5DE7</h2><p>\u6211\u4EEC\u90FD\u662F\u4E3A\u4E86\u60C5\u7EEA\u4EF7\u503C\u4E70\u5355</p><ol><li>\u611A\u8822\u8BF1\u9975\u5B9A\u4EF7 \u7528\u6237A\u548CB\u4E4B\u95F4\u5F88\u96BE\u505A\u51FA\u51B3\u7B56\u65F6\uFF0C\u7ED9\u7528\u6237\u4E00\u4E2A\u770B\u8D77\u6765\u5F88\u611A\u8822\u7684\u8BF1\u9975\u53C2\u7167\u65B9\u6848\uFF0C\u4F1A\u76F4\u63A5\u5F71\u54CD\u7528\u6237\u7684\u9009\u62E9 <img src="'+l+'" alt="9a1f4d0b3ee0d27ae753d3ae3618877d.png"></li></ol><p>\u4EF7\u683C\u951A\u5B9A\u6548\u5E94\uFF1A\u4E2D\u95F4\u9009\u9879\u662F\u591A\u4F59\u7684\uFF0C\u53EA\u662F\u4E3A\u4E86\u505A\u5BF9\u7167</p><ol start="2"><li>\u6346\u7ED1\u5B9A\u4EF7 \u6346\u7ED1\u5B9A\u4EF7\uFF0C\u4EE5\u4E00\u4E2A\u7EFC\u5408\u4EF7\u683C\u63D0\u4F9B\u4E24\u4E2A\u4EE5\u4E0A\u7684\u4EA7\u54C1\u548C\u670D\u52A1 <strong>\u9EA6\u5F53\u52B3\u5168\u5BB6\u6876</strong> \u597D\u5904\uFF1A 1.\uFF09 \u63A9\u9970\u4E2A\u522B\u4EF7\u683C 2.\uFF09\u9690\u85CF\u4EF7\u683C\u4E0A\u6DA8 3.\uFF09\u964D\u4F4E\u5FC3\u91CC\u4EF7\u683C\u8BC4\u4F30\u8FC7\u7A0B\u9003\u907F\u7ADE\u4E89 4.\uFF09\u8D85\u503C\u5E7B\u89C9\u7684\u4F53\u9A8C</li></ol><p>\u6838\u5FC3\uFF1A\u6253\u5305\u4EA7\u54C1\u8981\u6709\u5F3A\u76F8\u5173\u6027\uFF0C\u4FDD\u8BC1\u7528\u6237\u8FEB\u5207\u9700\u8981A\u4E5F\u4E0D\u6392\u65A5B</p><ol start="3"><li><p>\u951A\u5B9A\u5B9A\u4EF7 \u7ED9\u4F60\u60F3\u5356\u7684\u4E00\u4E2A\u5B9A\u4EF7 \u7231\u9A6C\u4ED5\u5E97\u91CC\u201C\u6C38\u8FDC\u4E0D\u5356\u201D\u7684200w\u624B\u8868 \u83DC\u5355\u4E0A\u8D35\u5F97\u79BB\u8C31\u7684\u83DC\uFF0C\u53EA\u662F\u7ED9\u4E2A\u5BF9\u6BD4\uFF0C\u8BA9\u4F60\u70B9\u5E73\u4EF7\u7684 \u5B9E\u9645\u4EF7\u683C\u65C1\u8FB9\u4E00\u76F4\u6709\u7684\u201C\u539F\u4EF7\u201D</p></li><li><p>\u4E2D\u95F4\u504F\u597D\u5B9A\u4EF7 \u5BF9\u4E8E\u4E0D\u591F\u719F\u6089\u7684\u4E1C\u897F\uFF0C\u4EBA\u4EEC\u559C\u6B22\u4E2D\u95F4\u4EF7\u4F4D\u7684 \u4F8B\u5982\uFF1A \u7EA2\u9152\uFF1A98/\u676F\uFF1B68/\u676F\uFF1B48/\u676F\uFF0870%\u7684\u4EBA\u4F1A\u900968\uFF09 \u7EA2\u9152\uFF1A128\uFF1B98\uFF1B68\uFF0868%\u7684\u4EBA\u4F1A\u900998\uFF09 \u5728\u4FE1\u606F\u4E0D\u5BF9\u79F0\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u4F1A\u9009\u62E9\u4E2D\u95F4\u4EF7\u4F4D\u7684\uFF0C\u4E2D\u95F4==\u5B89\u5168</p></li></ol><p>\u628A\u4F60\u6700\u60F3\u5356\u7684\u4EA7\u54C1\u653E\u5230\u201C\u4E2D\u95F4\u4EF7\u4F4D\u201D\uFF0C\u5206\u522B\u63D0\u4F9B\u66F4\u8D35\u66F4\u4F4E\u7684\u5BF9\u6BD4</p><h3 id="\u964D\u4EF7\u7684\u7B2C\u4E00\u539F\u5219-\u4E0D\u8981\u8F7B\u6613\u964D\u4EF7" tabindex="-1"><a class="header-anchor" href="#\u964D\u4EF7\u7684\u7B2C\u4E00\u539F\u5219-\u4E0D\u8981\u8F7B\u6613\u964D\u4EF7" aria-hidden="true">#</a> \u964D\u4EF7\u7684\u7B2C\u4E00\u539F\u5219\uFF1A\u4E0D\u8981\u8F7B\u6613\u964D\u4EF7</h3><p>\u5371\u5BB3\uFF1A \u5982\u679C\u4F60\u4E00\u76F4\u7ED9\u7528\u6237\u7684\u5370\u8C61\u90FD\u662F\u9AD8\u54C1\u4EF7\u4F18\u7684\uFF0C\u4F60\u964D\u4EF7\u4F1A\u8BA9\u4F60\u7684\u5F62\u8C61\u53D8\u5EC9\u4EF7\uFF0C\u5F71\u54CD\u590D\u8D2D \u5982\u679C\u7ECF\u5E38\u964D\u4EF7\uFF0C\u5BA2\u6237\u4F1A\u7B49\u4F60\u964D\u4EF7\u800C\u4E0D\u662F\u9A6C\u4E0A\u8D2D\u4E70</p><h3 id="\u4EC0\u4E48\u65F6\u5019\u9002\u5408\u964D\u4EF7" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u65F6\u5019\u9002\u5408\u964D\u4EF7" aria-hidden="true">#</a> \u4EC0\u4E48\u65F6\u5019\u9002\u5408\u964D\u4EF7\uFF1F</h3><p>\u5F53\u4EA7\u54C1\u8FDB\u5165\u8870\u9000\u671F\uFF0C\u53EF\u4EE5\u5728\u77ED\u671F\u5185\u8003\u8651\u964D\u4EF7\u4EE5\u62B5\u6D88\u9700\u6C42\u4E0B\u964D\u5E26\u6765\u7684\u5F71\u54CD \u6700\u91CD\u8981\u7684\u662F\u7814\u53D1\u63A8\u51FA\u7B26\u5408\u5E02\u573A\u9700\u6C42\u7684\u65B0\u4EA7\u54C1\uFF0C\u83B7\u53D6\u66F4\u9AD8\u5229\u6DA6\uFF0C\u540C\u65F6\u8BA9\u8870\u9000\u7684\u4EA7\u54C1\u4E0B\u5E02</p><p>\u745E\u5E78\uFF1A\u201C\u5976\u8336\u5316\u5496\u5561\u201D\u7684\u5FC3\u667A\u5F00\u521B \u5B83\u6446\u8131\u4E86\u89C4\u6A21\u5316\u6269\u5F20\u7684\u9677\u9631\uFF0C\u800C\u662F\u5F00\u62D3\u201C\u5976\u8336\u201D\u5E02\u573A \u6C38\u8FDC\u4E0D\u8981\u9677\u5165\u4EF7\u683C\u6BD4\u62FC\uFF0C\u521B\u9020\u9AD8\u4EF7\u503C\uFF0C\u83B7\u53D6\u9AD8\u5229\u6DA6\u624D\u662F\u91CD\u70B9</p><h2 id="\u91CD\u65B0\u7406\u89E3\u5B9A\u4EF7" tabindex="-1"><a class="header-anchor" href="#\u91CD\u65B0\u7406\u89E3\u5B9A\u4EF7" aria-hidden="true">#</a> \u91CD\u65B0\u7406\u89E3\u5B9A\u4EF7</h2><p>\u5B9A\u4EF7\uFF1A\u901A\u8FC7\u4EF7\u683C\u5B9E\u73B0\u5229\u76CA\u6700\u5927\u5316\u7684\u65B9\u6CD5\u8BBA <img src="'+d+'" alt="fea3a0a8ddbc52e7eee80e6e122ca9ba.png"></p><p><img src="'+o+'" alt="314b12c7df3fad68c70f86b98cfdb0de.png"></p><p>\u4F60\u81EA\u5DF1\u8BA4\u77E5\u4F60\u7684\u4EA7\u54C1\u662F\u4EC0\u4E48\u4F4D\u7F6E</p><p>\u8D8A\u4F4E\u7AEF\uFF0C\u8D8A\u6CE8\u91CD<strong>\u529F\u80FD</strong> \u8D8A\u9AD8\u7AEF\uFF0C\u8D8A\u6CE8\u91CD<strong>\u60C5\u611F</strong></p><h3 id="\u5B9A\u751F\u6001\u4F4D" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u751F\u6001\u4F4D" aria-hidden="true">#</a> \u5B9A\u751F\u6001\u4F4D</h3><p>\u751F\u6001\u4F4D\u6CA1\u6709\u9AD8\u4E0B\uFF0C\u53EA\u6709\u5339\u914D\u5EA6 \u50CF\u4E00\u4E2A\u6263\u5B50\uFF0C\u53EF\u80FD\u505A\u5230\u51E0\u4E2A\u4EBF\u7684\u5229\u6DA6\uFF08\u4F4E\u7AEF\u7684\u4EA7\u54C1\uFF0C\u4E3B\u8981\u8003\u8651\u4F9B\u5E94\u94FE\u548C\u6210\u672C\uFF09 \u5F53\u4F60\u9009\u62E9\u4E00\u4E2A\u751F\u6001\u4F4D\uFF0C\u5176\u5B9E\u5DF2\u7ECF\u521D\u6B65\u9009\u5B9A\u4E86\u4E00\u4E2A\u4EF7\u683C\u533A\u95F4</p><p><img src="'+t+'" alt="953314bacea63391eacbe46c3826f57a.png"></p><h3 id="\u5B9A\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u6A21\u5F0F" aria-hidden="true">#</a> \u5B9A\u6A21\u5F0F</h3><p>\u627E\u5230\u6700\u5927\u7684\u5229\u6DA6\u70B9\uFF1A\u9500\u91CF\u548C\u4EF7\u683C\u7684\u5E73\u8861\u70B9</p><p>\u7528\u6237\u95EE\u5377\u6D4B\u4EF7\u683C\u6CA1\u6709\u7528\uFF0C\u9700\u8981\u771F\u5B9E\u7684\u4EF7\u683C\u5B9E\u9A8C \u4EF7\u683C\u5B9E\u9A8C\u539F\u5219\uFF1A</p><ol><li>\u4ED8\u6B3E\u624D\u7B97</li><li>\u4ECE\u9AD8\u5230\u4F4E\u6D4B\u8BD5\uFF0C\u6D4B\u5230\u6EE1\u610F\u503C\uFF08\u5229\u6DA6\uFF09\u53EF\u4EE5\u505C</li><li>\u4E0D\u8981\u8FC7\u5EA6\u5B9E\u9A8C\uFF1A\u5343\u5143\u4EA7\u54C1\u6D4B\u5230\u767E\u4F4D\u5C31\u597D</li></ol>',73),s=[c];function n(b,u){return e(),i("div",null,s)}var g=a(h,[["render",n],["__file","index.html.vue"]]);export{g as default};
